package Wipro;

public class SumofNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1=args[0];
		String s2=args[1];
		int a=Integer.parseInt(s1);
		int b=Integer.parseInt(s2);
		System.out.println("Sum of two numbers "+a+ " and "+b+" is: "+(a+b));
	}

}
